require('source-map-support').install();
var xl = require('./distribution');

function generateWorkbook() {
    var wb = new xl.Workbook({
        defaultFont: {
            name: 'Verdana',
            size: 12
        },
        logLevel: 5
    });

    var borders = wb.createStyle({
        border: {
            left: {
                style: 'thin',
                color: 'Black'
            },
            right: {
                style: 'thin',
                color: 'Black'
            },
            top: {
                style: 'thin',
                color: 'Black'
            },
            bottom: {
                style: 'thin',
                color: 'Black'
            }
        }
    });

    var ws = wb.addWorksheet('sheet');

    ws.cell(2, 2, 8, 3).style(borders);
    ws.cell(4, 1, 5, 5).style(borders);

    return wb;
}

generateWorkbook().write('Borders.xlsx');